package loopExample;

public class CharSetEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char ch;
		
		for(ch = 65; ch <123;ch ++) {
			System.out.print(ch+",");
		}
		System.out.println("");
		for(ch =  12593;ch < 12686; ch++) {
			System.out.print(ch);
			
		}
		
		System.out.println("=======================");
		//99dan
		int dan = 6;
		for(int i = 1;i<=9;i++) {
			System.out.println(dan+" x "+i+" = "+dan*i);
			
		}
	
	
	
	}

}
